import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import Layout from "../../components/Layout";
import Card from "../../components/UI/Card";
import CartItem from "./CartItem";
import { addToCart, getCartItems, removeCartItem } from "../../actions";
import PriceDetails from "../../components/PriceDetails";

import "./style.css";
import { MaterialButton } from "../../components/MaterialUI";

const CartPage = (props) => {
  const giftValue = 100; //static value
  const [gitsSelected, setGiftsSelected] = useState(0);
  const [priceTTC, setPriceTTC] = useState(0);
  const [priceAfterDiscount, setPriceAfterDiscount] = useState(0);
  const cart = useSelector((state) => state.cart);
  const auth = useSelector((state) => state.auth);
  const products = useSelector((state) => state.product);
  const [cartItems, setCartItems] = useState(cart.cartItems);
  const dispatch = useDispatch();

  useEffect(() => {
    setCartItems(cart.cartItems);
  }, [cart.cartItems]);

  useEffect(() => {
    if (auth.authenticate) {
      dispatch(getCartItems());
    }
  }, [auth.authenticate]);

  const onQuantityIncrement = (_id, qty) => {
    const { name, price, img } = cartItems[_id];
    cartItems[_id].price = products.productDetails.prix_ttc;
    dispatch(addToCart({ _id, name, price, img }, 1));
  };

  const onQuantityDecrement = (_id, qty) => {
    const { name, price, img } = cartItems[_id];
    dispatch(addToCart({ _id, name, price, img }, -1));
  };

  const onRemoveCartItem = (_id) => {
    dispatch(removeCartItem({ productId: _id }));
  };

  if (props.onlyCartItems) {
    return (
      <>
        {Object.keys(cartItems).map((key, index) => (
          <CartItem
            key={index}
            cartItem={cartItems[key]}
            onQuantityInc={onQuantityIncrement}
            onQuantityDec={onQuantityDecrement}
          />
        ))}
      </>
    );
  }
  const pricettc = () => {
    Object.keys(cart.cartItems).reduce((totalPrice, key) => {
      const { price, qty } = cart.cartItems[key];
      products.products.map((item) => {
        if (key === item._id) cart.cartItems[key].price = item.prix_ttc;
      });
      return totalPrice + price * qty;
    }, 0);
  };

  const priceAfterReduction = () => {
    Object.keys(cart.cartItems).reduce((totalPrice, key) => {
      const resut = 0;
      const { price, qty } = cart.cartItems[key];
      products.products.map((item) => {
        if (key === item._id) {
          cart.cartItems[key].price = item.prix_ttc;
          if (
            item.is_gift &&
            gitsSelected > 0 &&
            totalPrice > gitsSelected * 100
          ) {
            console.log("result", resut, gitsSelected);
            totalPrice = totalPrice + price * qty * 0.3;
          } else {
            totalPrice = totalPrice + price * qty - auth.user.remise_defaut;
            return totalPrice;
          }
        }
      });
      setPriceAfterDiscount(totalPrice);
    }, 0);
  };
  return (
    <Layout>
      <div className="cartContainer" style={{ alignItems: "flex-start" }}>
        <Card
          headerLeft={`My Cart`}
          headerRight={
            <>
              <div
                style={{
                  display: "flex",
                  flexDirection: "col",
                  border: "1 px solid grey",
                }}
              >
                <label style={{ color: "#2874F0" }}>
                  Total gifts Amount:
                  {auth.user.nbr_gifts * giftValue}DT
                </label>
              </div>
              <div
                style={{
                  display: "flex",
                  flexDirection: "col",
                  border: "1 px solid grey",
                }}
              >
                <div>
                  {
                    <input
                      type="range"
                      min="0"
                      max={auth.user.nbr_gifts}
                      value={gitsSelected}
                      onChange={(e) => {
                        setGiftsSelected(e.target.value);
                      }}
                    />
                  }
                </div>
                <label style={{ color: "#2874F0" }}>
                  ({gitsSelected}gift used)
                </label>
              </div>
            </>
          }
          style={{ width: "calc(100% - 400px)", overflow: "hidden" }}
        >
          {Object.keys(cartItems).map((key, index) => (
            <CartItem
              key={index}
              cartItem={cartItems[key]}
              onQuantityInc={onQuantityIncrement}
              onQuantityDec={onQuantityDecrement}
              onRemoveCartItem={onRemoveCartItem}
            />
          ))}

          <div
            style={{
              width: "100%",
              display: "flex",
              background: "#ffffff",
              justifyContent: "flex-end",
              boxShadow: "0 0 10px 10px #eee",
              padding: "10px 0",
              boxSizing: "border-box",
            }}
          ></div>
        </Card>
        <PriceDetails
          totalItem={Object.keys(cart.cartItems).reduce(function (qty, key) {
            return qty + cart.cartItems[key].qty;
          }, 0)}
          totalPrice={pricettc()}
          totalPriceAfterReuction={priceAfterReduction()}
        />
      </div>
    </Layout>
  );
};

export default CartPage;
