import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProductDetailsById } from "../../actions";
import Layout from "../../components/Layout";
import { IoIosArrowForward, IoIosStar, IoMdCart } from "react-icons/io";
import { BiRupee } from "react-icons/bi";
import { AiFillThunderbolt } from "react-icons/ai";
import { MaterialButton } from "../../components/MaterialUI";
import "./style.css";
import { addToCart } from "../../actions";

/**
 * @author
 * @function ProductDetailsPage
 **/

const ProductDetailsPage = (props) => {
  const dispatch = useDispatch();
  const product = useSelector((state) => state.product);

  useEffect(() => {
    const { productId } = props.match.params.productSlug;
    console.log("productId", productId);
    const payload = {
      params: {
        productId,
      },
    };
    dispatch(getProductDetailsById(payload));
  }, []);

  if (product.length === 0) {
    return null;
  }
  console.log("product", product.productDetails.en_stock);
  return (
    <Layout>
      {/* <div>{product.productDetails.name}</div> */}
      <div className="productDescriptionContainer">
        <div className="flexRow">
          <div className="verticalImageStack">
            <div className="thumbnail">
              <img
                src={
                  "data:image/jpeg;base64,"
                }
              />
            </div>
          </div>
          <div className="productDescContainer">
            <div className="productDescImgContainer">
              <img
                src={
                  "data:image/jpeg;base64,"
                }
                alt={``}
              />
            </div>

            {/* action buttons */}
            <div className="flexRow">
              {product.productDetails.en_stock ? (
                <>
                  <MaterialButton
                    title="ADD TO CART"
                    bgColor="#ff9f00"
                    textColor="#ffffff"
                    style={{
                      marginRight: "5px",
                    }}
                    icon={<IoMdCart />}
                    onClick={() => {
                      const { _id, libelle, prix_ttc, is_gift, en_stock } =
                        product.productDetails;

                      dispatch(
                        addToCart({ _id, libelle, prix_ttc, is_gift, en_stock })
                      );
                      props.history.push(`/cart`);
                    }}
                  />
                  <MaterialButton
                    title="BUY NOW"
                    bgColor="#fb641b"
                    textColor="#ffffff"
                    style={{
                      marginLeft: "5px",
                    }}
                    icon={<AiFillThunderbolt />}
                  />
                </>
              ) : (
                <div></div>
              )}
            </div>
          </div>
        </div>
        <div>
          {/* home > category > subCategory > productName */}

          {/* product description */}
          <div className="productDetails">
            <p className="productTitle">{product.libelle}</p>
            <div>
              <span className="ratingCount">
                4.3 <IoIosStar />
              </span>
            </div>
            <div className="extraOffer">Extra 4500 off </div>
            <div className="flexRow priceContainer">
              <span className="price">{product.productDetails.prix_ttc}DT</span>
              <span className="discount" style={{ margin: "0 10px" }}>
                22% off
              </span>
              {/* <span>i</span> */}
            </div>
            <div>
              <p
                style={{
                  color: "#212121",
                  fontSize: "14px",
                  fontWeight: "600",
                }}
              >
                {product.productDetails.en_stock
                  ? "Available Offers"
                  : "not available"}
              </p>
              <p style={{ display: "flex" }}>
                <span
                  style={{
                    width: "100px",
                    fontSize: "12px",
                    color: "#878787",
                    fontWeight: "600",
                    marginRight: "20px",
                  }}
                >
                  Description
                </span>
                <span
                  style={{
                    fontSize: "12px",
                    color: "#212121",
                  }}
                >
                  {product.productDetails.description}
                </span>
              </p>
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default ProductDetailsPage;
